import React, { useState } from 'react';
import styled from 'styled-components'; 
function Today() {
    const [todayLists, setTodayLists] = useState(['이거하기','저거하기'])
    return (
        <StyledToday>
            <div className='title'>오늘 할 일</div>
            <ol className='lists-con'>
                {todayLists.map((list,i)=>
                <li className="list" key={i}>
                    {list}
                    <span className='delete'>삭제</span>
                </li>)}
            </ol>
            <input></input>
        </StyledToday>
    )
}
const StyledToday = styled.div`
  width: 50%;
  background-color: rgba(240, 237, 255,0.5);
  border-radius: 10px;
  padding: 30px;
  margin: 30px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;

  .title{
      font-size: 1.2rem;
      color: gray;
      font-weight: bold;
      text-align: center
  }
  .lists-con{
      list-style: none;
      padding: 0;
  }
  .list{
      display: flex;
      align-items: center;
      height: 25px;
      border-bottom: 1px solid gray;
      margin-bottom: 10px;
      padding: 3px;
  }
  .delete{
      padding: 3px;
      color: gray;
      margin-left: auto;
      cursor: pointer;
  }
  .delete:hover{
      font-weight: bold;
  }
  input{
      margin-top: auto;
  }
`;
export default Today
