import React from 'react'
import styled from 'styled-components';
import Today from './main/Today';
import Tomorrow from './main/Tomorrow';
function Main({mode}) {
    const showLists = (mode) =>{
        switch (mode) {
            case 'today':
                return <Today only/>
            case 'tomorrow':
                return <Tomorrow only/>
            case 'all':
                return <>
                <Today only={false}/>
                <Tomorrow only={false}/>
                </>
            default:
                break;
        }
    }
    return (
        <StyledMain>
            {showLists(mode)}
        </StyledMain>
    )
}
const StyledMain = styled.div`
  width: 100%;
  flex-grow: 1;
  display: flex;
  margin-top: 10px;
`;
export default Main
